import { Account, Chain, CustomTransport, PublicClient, PublicClientConfig, WalletClient, WalletClientConfig } from 'viem';
import { BaseWallet } from '@dynamic-labs/types';
/**
 * Params for the createPublicClient method of Dynamic's client
 */
export type PublicClientConfigDynamic<C extends Chain | undefined> = Omit<PublicClientConfig<CustomTransport, C>, 'transport'> & {
    chain: NonNullable<PublicClientConfig<CustomTransport, C>['chain']>;
};
/**
 * Params for the createWalletClient method of Dynamic's client
 */
export type WalletClientConfigDynamic<C extends Chain | undefined> = Omit<WalletClientConfig<CustomTransport, C>, 'transport' | 'account'> & {
    wallet: BaseWallet;
};
export type IViemExtension = {
    viem: {
        /**
         * Creates a public viem client, using a custom Dynamic transport
         */
        createPublicClient: <C extends Chain | undefined = undefined>(params: PublicClientConfigDynamic<C>) => PublicClient<CustomTransport, C>;
        /**
         * Creates a wallet viem client, using a custom Dynamic transport
         */
        createWalletClient: <C extends Chain = Chain>(params: WalletClientConfigDynamic<C>) => Promise<WalletClient<CustomTransport, C, Account>>;
    };
};
